svg.script:

const githubUrl = 'https://raw.githubusercontent.com/helloflygh/Titanv3/main/startup';

async function launchGame() {
    console.log("Attempting to fetch and launch...");
    try {
        const response = await fetch(githubUrl);
        if (!response.ok) throw new Error('Network response was not ok');
        const html = await response.text();

        const newWindow = window.open('about:blank', '_blank');

        if (newWindow) {
            newWindow.document.open();
            newWindow.document.write(html);
            newWindow.document.close();
        } else {
            alert("Pop-up blocked! Please allow pop-ups for this SVG.");
        }
    } catch (err) {
        console.error("Launch failed:", err);
        alert("Error loading game: " + err.message);
    }
}

// Explicitly target the background rect for the click
document.getElementById('clickable-surface').addEventListener('click', launchGame);
