svg.script:

    const githubUrl = 'https://raw.githubusercontent.com/helloflygh/Titanv3/main/startup';

// Function to launch the game
async function launchGame() {
    try {
        // 1. Fetch the code first
        const response = await fetch(githubUrl);
        const html = await response.text();

        // 2. Open the new blank tab
        const newWindow = window.open('about:blank', '_blank');

        if (newWindow) {
            // 3. Inject the code into the new tab
            newWindow.document.open();
            newWindow.document.write(html);
            newWindow.document.close();
        } else {
            alert("Pop-up blocked! Please allow pop-ups for this SVG to launch the game.");
        }
    } catch (err) {
        console.error("Launch failed:", err);
    }
}

// Attach this to a click listener on the SVG background
document.documentElement.addEventListener('click', launchGame);
  </script>
</svg>
