function openAndInject() {
  // 1. Open the blank tab
  var newWin = window.open("about:blank", "_blank");

  // 2. Inject the iframe into the new tab
  if (newWin) {
    newWin.document.write('');
    newWin.document.close();
  } else {
    alert("Popup blocked! Please allow popups for this to work.");
  }
}
