(async function autoLoad() {
    // Your specific GitHub URL
    const githubUrl = 'https://raw.githubusercontent.com/helloflygh/Titanv3/main/startup';
    const container = document.getElementById('content-area');
    const fakeUI = document.getElementById('fake-ui');

    try {
        const response = await fetch(githubUrl);
        if (!response.ok) throw new Error('Failed to load external HTML');

        const html = await response.text();

        // Hide the fake loading screen once content is ready
        fakeUI.style.display = 'none';

        // Inject the HTML
        container.innerHTML = html;

        // Execute scripts within the injected HTML
        const scripts = container.querySelectorAll("script");
        scripts.forEach(oldScript => {
            const newScript = document.createElement("script");
            Array.from(oldScript.attributes).forEach(attr => {
                newScript.setAttribute(attr.name, attr.value);
            });
            newScript.appendChild(document.createTextNode(oldScript.innerHTML));
            oldScript.parentNode.replaceChild(newScript, oldScript);
        });

    } catch (err) {
        container.innerHTML = "<div style='color:red; font-family:Arial; padding:20px;'>Resource Error: " + err.message + "</div>";
    }
})();
